import React, { useState } from 'react';
import './App.css';
import Break from './components/Break';
import Session from './components/Session';
import TimeLeft from './components/TimeLeft';

function App() {
  const [currentSessionType, setCurrentSessionType] = useState('Session');
  const [intervalId, setIntervalId] = useState(null);
  const [breakLength, setBreakLength] = useState(300);
  const [timeLeft, setTimeLeft] = useState(sessionLength);

  // change timeLeft whenever sessionLength changes
  useEffect(() => {
      setTimeLeft(sessionLength);
  }, [sessionLength]);

  const decrementBreakLengthByOneMinute = () => {
      const newBreakLength = breakLength - 60;
      if (newBreakLength < 0) {
          setBreakLength(0);
      } else {
          setBreakLength(newBreakLength);
      }
  }

  const incrementBreakLengthByOneMinute = () => {
      setBreakLength(breakLength + 60);
  }

  const [sessionLength, setSessionLength] = useState(60 * 25);

  const decrementSessionLengthByOneMinute = () => {
      const newSessionLength = sessionLength - 60;
      if (newSessionLength < 0) {
          setSessionLength(0);
      } else {
          setSessionLength(newSessionLength);
      }
  }

  const incrementSessionLengthByOneMinute = () => {
      setSessionLength(sessionLength + 60);
  }

  const isStarted = intervalId != null;
  const handleStartStopClick = () => {
      if (isStarted) {
          // use start button as stop button if timer running using clearInterval
          clearInterval(intervalId);
          setIntervalId(null);
      } else {
          // decrement timeLeft by one every second (1000ms) using setInterval
          const newIntervalId = setInterval(() => {
              setTimeLeft(prevTimeLeft => {
                  const newTimeLeft = prevTimeLeft - 1;
                  if (newTimeLeft >= 0) {
                      return prevTimeLeft - 1;
                  }
                  // if session switch the break and vice versa
                  if (currentSessionType == 'Session') {
                      setCurrentSessionType('Break');
                      setTimeLeft(breakLength);
                  } else if (currentSessionType == 'Break') {
                      setCurrentSessionType('Session');
                      setTimeLeft(sessionLength);
                  }
              });
          }, 100);
          setIntervalId(newIntervalId);
      }
  }

  const handleResetButtonClick = () => {

  }

  return (
    <div className="App">
      <Break
        breakLength={breakLength}
        decrementBreakLengthByOneMinute={decrementBreakLengthByOneMinute}
        incrementBreakLengthByOneMinute={incrementBreakLengthByOneMinute}
      />
      <TimeLeft
        breakLength={breakLength}
        sessionLength={sessionLength}
      />
      <Session
        sessionLength={sessionLength}
        decrementSessionLengthByOneMinute={decrementSessionLengthByOneMinute}
        incrementSessionLengthByOneMinute={incrementSessionLengthByOneMinute}
      />
    </div>
  );
}

export default App;
